"use strict";(self.webpackChunkcreate_project_docs=self.webpackChunkcreate_project_docs||[]).push([[8888],{7167:e=>{e.exports=JSON.parse('{"title":"Requirements Specification","description":"ARPetPals is an engaging augmented reality (AR) application for Android, designed to integrate health and fitness into a virtual pet experience. It requires Android devices (version 8.0 or later) with a functioning camera, microphone, and speaker. Developed using Unity 3D (version 2022.2.21) and compatible with tools like Xcode, VScode, GitHub, and Jira, the app leverages AI for food detection and object recognition to influence the health and happiness of users\' virtual pets based on their real-world diet and activity.","slug":"/category/requirements-specification","permalink":"/project-ar-pet-pals/docs/category/requirements-specification","navigation":{"previous":{"title":"Introduction","permalink":"/project-ar-pet-pals/docs/intro"},"next":{"title":"Activities","permalink":"/project-ar-pet-pals/docs/requirements/activities"}}}')}}]);